mapper.select:
select * from "user" where user_name = #{user_name} and
user_email =
#{user_email}

mapper.update:
UPDATE "user"
user_pwd = #{user_pwd}, user_name = #{user_name}, user_tel = #{user_tel}, user_ath = #{user_ath}, user_crr = #{user_crr}, user_age = #{user_age}, user_job_ttl = #{user_job_ttl}, user_job_cnt = #{user_job_cnt}, user_type = #{user_type}, ether_id = #{ether_id},
WHERE USER_ID =#{user_id}

UPDATE APPLICATION
SET APP_INVSB=1 WHERE app_id =
#{user_id}

mapper.select:
SELECT U.USER_NAME, U.USER_CRR, U.USER_TEL, T.TM_DEPT 
FROM "user" U, TEAM T 
WHERE U.USER_ID = #{user_id} AND U.USER_ID = T.USER_ID and t.prj_id=#{prj_id}
